  public static byte [] decode (final String s)
  {
    final ICommonsList <String> aNums = new CommonsArrayList <> ();
    StringHelper.explode (',', s, x -> aNums.add (x.trim ()));

    final byte [] ret = new byte [aNums.size ()];
    int i = 0;
    for (final String sPart : aNums)
      ret[i++] = (byte) Integer.parseInt (sPart);
    return ret;
  }
